mapper.insert:

	SELECT NVL(MAX(eat_no),0) + 1
	FROM approvalstemp

INSERT INTO approvalstemp (
eat_no
,title
,content
)
VALUES (
#{eatNo}
,#{title}
,#{content}
)

mapper.update:
UPDATE approvalstemp
SET
title = #{title}
,content = #{content}
WHERE eat_no = #{eatNo}

mapper.insert:

	SELECT NVL(MAX(approvals.ea_no),0) + 1
	FROM approvals

INSERT INTO approvals(
approvals.ea_no
,title
,eat_no
,approvals.reg_date
,content
,emp_no
)
VALUES (
#{eaNo}
,#{title}
,#{eatNo}
,sysdate
,#{content}
,#{drafterEmpNo}
)


	SELECT NVL(MAX(ear_no), 0) + 1 FROM approvers

INSERT ALL


		INTO approvers (
		ear_no
		,ea_no
		,emp_no
		,sort
		)
		VALUES (
		FIND_APPROVERS_SEQ()
		,#{approvers.eaNo}
		,#{approvers.empNo}
		,#{approvers.sort}
		)


SELECT * FROM dual

mapper.update:
update approvals
set
content = #{content}
,title = #{title}
WHERE ea_no = #{eaNo}

UPDATE approvers
set emp_no = #{empNo}
,sort = #{sort}
where ea_no = #{eaNo}
